mapper.insert:
        INSERT INTO API_MST_PARAM
               (
				SEQ
				,API_ID
				,ENAME
				,KNAME
                ) 
        VALUES (
                (SELECT ifnull(MAX_SEQ, 1) 
                   FROM 
                      (
	                  SELECT MAX(seq)+1 AS MAX_SEQ 
	                   FROM API_MST_PARAM 
	                  WHERE API_ID = #{API_MST_PARAM.api_id} 
	                  ) A
                  ),
                #{API_MST_PARAM.api_id},
                #{API_MST_PARAM.ename},
                #{API_MST_PARAM.kname}
                )
