mapper.select:
SELECT x.MENU_ID, x.MENU_NM, x.SEQ, x.LEVEL, x.USE_YN, x.PARENT_MENU_ID, x.ISDIRECTORY, x.DIVISION FROM ( SELECT distinct a.MENU_ID, a.MENU_NM, a.SEQ, a.LEVEL, a.USE_YN, a.PARENT_MENU_ID, a.ISDIRECTORY, a.DIVISION FROM SYS_MENU_MST a join SYS_MENU_USR b on a.DIVISION = b.DIVISION and a.MENU_ID = b.MENU_ID and a.SEQ = b.SEQ
join SYS_MENU_AUTH c on a.DIVISION = c.DIVISION and a.MENU_ID = c.MENU_ID
WHERE b.SPIKE_ID = #{SYS_MENU_USR.spike_id}
and ( c.depart_cd = #{SYS_MENU_USR.dept} or c.depart_cd = ( CASE WHEN #{SYS_MENU_USR.dept} = '0040' then 'ALL' WHEN #{SYS_MENU_USR.dept} = '0020' then 'ALL' WHEN #{SYS_MENU_USR.dept} = '2000' then 'ALL' end ) )
) x ORDER BY x.DIVISION DESC, x.SEQ

SELECT x.MENU_ID, x.MENU_NM, x.SEQ, x.LEVEL, x.USE_YN, x.PARENT_MENU_ID, x.ISDIRECTORY, x.DIVISION, case when x.ISDIRECTORY is null then x.OWNER else null end OWNER FROM ( SELECT distinct a.MENU_ID, a.MENU_NM, a.SEQ, a.LEVEL, a.USE_YN, a.PARENT_MENU_ID, a.ISDIRECTORY, a.DIVISION, a.OWNER FROM SYS_MENU_MST a join SYS_MENU_USR b on a.DIVISION = b.DIVISION and a.MENU_ID = b.MENU_ID and a.SEQ = b.SEQ
join SYS_MENU_AUTH c on a.DIVISION = c.DIVISION and a.MENU_ID = c.MENU_ID
WHERE b.SPIKE_ID = #{SYS_MENU_USR.spike_id}
and ( c.depart_cd = #{SYS_MENU_USR.dept} or c.depart_cd = ( CASE WHEN #{SYS_MENU_USR.dept} = '0040' then 'ALL' WHEN #{SYS_MENU_USR.dept} = '0020' then 'ALL' WHEN #{SYS_MENU_USR.dept} = '2000' then 'ALL' end ) )
) x ORDER BY x.DIVISION DESC, x.SEQ

        SELECT * FROM SYS_MENU_USR 
        WHERE SPIKE_ID = #{SYS_MENU_USR.spike_id}
          AND DIVISION = #{SYS_MENU_USR.division}
		  AND MENU_ID = #{SYS_MENU_USR.menu_id}

mapper.update:
UPDATE SYS_MENU_USR
   SET USE_YN =  #{SYS_MENU_USR.use_yn} 
 WHERE SPIKE_ID = #{SYS_MENU_USR.spike_id}
   AND DIVISION = #{SYS_MENU_USR.division}
   AND MENU_ID = #{SYS_MENU_USR.menu_id}
